targets.expr:
sum(
  rate(container_cpu_usage_seconds_total{pod_name=~"nginx-.*", instance=~"$instance"}[$__interval_rv])
  * on (pod_name) group_left (app) max by (pod_name, app) (
    label_replace(
      label_replace(kube_pod_labels{label_app=~"$app"}, "pod_name", "$1", "pod", "(.*)"),
      "app","$1","label_app","(.*)"
    )
  )
  * on (namespace) group_left (controller) max by (namespace, controller) (
    label_replace(kube_namespace_labels{label_controller=~"$controller"}, "controller", "$1", "label_controller", "(.*)")
  )
)

