sum by (namespace, controller, persistentvolumeclaim) (
  max by (namespace, controller, persistentvolumeclaim)
  (
    sum by (namespace, persistentvolumeclaim) (
        max_over_time(kubelet_volume_stats_capacity_bytes{namespace="$namespace"}[$__interval])
    ) 

    * max by (namespace, persistentvolumeclaim) (
        max_over_time(kube_persistentvolumeclaim_info{namespace="$namespace", storageclass!~"local.+"}[$__interval])
    )

        * on (namespace, persistentvolumeclaim) group_left(pod) 
    max by (namespace, persistentvolumeclaim, pod) (
         max_over_time(kube_pod_spec_volumes_persistentvolumeclaims_info{namespace="$namespace"}[$__interval])
    ) 

       * on (namespace, pod) group_left(controller)
      kube_controller_pod{namespace="$namespace", controller=~"$controller"} 
  )
)

