targets.expr:
sum(
  kube_endpoint_address_available{job="kube-state-metrics", namespace=~"$namespace", endpoint=~"$service_name"} and on (namespace, endpoint) label_replace(nginx_requests_total:details{job="nginx-ingress-controller", namespace=~"$namespace", ingress_name=~"$ingress_name", scheme=~"http|https", server_name=~"$server_name", service_name!="None"}, "endpoint", "$1", "service_name", "(.*)")
)

sum(
  (kube_endpoint_address_available{job="kube-state-metrics", namespace=~"$namespace"} and on (namespace, endpoint) label_replace(nginx_requests_total:details{job="nginx-ingress-controller", namespace=~"$namespace", ingress_name=~"$ingress_name", scheme=~"http|https", server_name=~"$server_name", service_name!="None"}, "endpoint", "$1", "service_name", "(.*)"))
  - (kube_endpoint_address_not_ready{job="kube-state-metrics", namespace=~"$namespace"} and on (namespace, endpoint) label_replace(nginx_requests_total:details{job="nginx-ingress-controller", namespace=~"$namespace", ingress_name=~"$ingress_name", scheme=~"http|https", server_name=~"$server_name", service_name!="None"}, "endpoint", "$1", "service_name", "(.*)"))
) / sum(
  kube_endpoint_address_available{job="kube-state-metrics", namespace=~"$namespace", endpoint=~"$service_name"} and on (namespace, endpoint) label_replace(nginx_requests_total:details{job="nginx-ingress-controller", namespace=~"$namespace", ingress_name=~"$ingress_name", scheme=~"http|https", server_name=~"$server_name", service_name!="None"}, "endpoint", "$1", "service_name", "(.*)") > 0
)
